import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import ProductCard from './product-Card';
import SvgWrapper from './svg-wrapper';

const GlideSlider = ({ slides }) => {
  const customPrevArrow = (
    <button className="custom-prev-arrow">
        <SvgWrapper src='left' ></SvgWrapper>
    </button>
  );

  const customNextArrow = (
    <button className="custom-next-arrow">
        <SvgWrapper src='right' ></SvgWrapper>
    </button>
  );

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1,
    centerMode: true,
    focusOnSelect: true,
    // autoplay: true,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 4,
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 3,
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
        }
      }
    ],
    prevArrow: customPrevArrow,
    nextArrow: customNextArrow,
    appendArrows: null, // Hide default arrows

  };

  return (
    <Slider {...settings}>
      {slides.map((slide, index) => (
        <div key={index} >
          <ProductCard
            image={slide.image}
            title={slide.title}
            description={slide.description}
          />
        </div>
      ))}
    </Slider>
  );
};

export default GlideSlider;
