import React, { useEffect, useRef } from "react";
import { Sweeper } from "react-sweeper";
import "@glidejs/glide/dist/css/glide.core.min.css";
import "@glidejs/glide/dist/css/glide.theme.min.css";
import ProductCard from "./product-Card";

const GlideSlider = ({ slides, className }) => {
  const sweeperRef = useRef(null);

  useEffect(() => {
    sweeperRef.current.update();
  }, [slides]);

  return (
    <Sweeper
      className={`glide ${className}`}
      ref={sweeperRef}
      options={{
        type: "carousel",
        perView: 5,
        gap: 40,
        focusAt: "center",
        hoverpause: true,
        breakpoints: {
          1024: { perView: 4 },
          768: { perView: 3 },
          480: { perView: 2 },
        },
      }}
    >
      {slides.map((slide, index) => (
        <div key={index}>
          <ProductCard
            image={slide.image}
            title={slide.title}
            description={slide.description}
            className="product-card"
          />
        </div>
      ))}
    </Sweeper>
  );
};

export default GlideSlider;
