import React, { useEffect, useState } from 'react';
import './sidebar.css'

const context ={

}
function Sidebar({closeSidebar}) {
  const [activeChildIndex, setActiveChildIndex] = useState(null);
  const [isMounted, setIsMounted] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);
  const [showSidebarNav, setShowSidebarNav] = useState(true);
  const [sidebarl2Nav, setSidebarl2Nav] = useState({});
  const [sidebarl3Nav, setSidebarl3Nav] = useState({});
  const [mobileNavRedirectionLink] = useState({
    "contact-us": "",
    "store-locator": "/locate-us",
    "track-order": "",
    "country-selector": "",
  });

  useEffect(() => {
    setIsMounted(true);
    console.log("mounted NavData", context);
    return () => {
      setIsMounted(false);
    };
  }, []);

  useEffect(() => {
    // Watch route changes
    const handleRouteChange = () => {
      closeSidebarNav();
    };
    window.addEventListener('popstate', handleRouteChange);
    return () => {
      window.removeEventListener('popstate', handleRouteChange);
    };
  }, []);

  useEffect(() => {
    // To stop document scroll when modal is active
    if (showSidebar) {
      document.body.style.position = "fixed";
      document.body.style.top = `-${window.scrollY}px`;
    } else {
      const scrollY = parseInt(document.body.style.top || "0");
      document.body.style.position = "";
      document.body.style.top = "";
      window.scrollTo(0, Math.abs(scrollY));
    }
  }, [showSidebar]);

  const getNavigation = context?.navigation?.slice(0, 100);
  const isDesktop = context?.navigation?.length > 0 && !'isSidebarNav';

  const getMenuOffset = () => {
    let val = 'layout_1'
    return val === "layout_1" || val === "layout_2"
      ? "padding-top: 20px"
      : "padding-top: 4px";
  };

  const toggleChild = (index) => {
    setActiveChildIndex(prevIndex => prevIndex === index ? null : index);
  };

  const isChildOpen = (index) => {
    return activeChildIndex === index;
  };

  const clearSearch = () => {
    // Handle clearing search input
  };

  const getGlobalConfigValue = () => {
    // Implement this function
  };

  const openSidebarNav = () => {
    setShowSidebar(true);
  };

  const closeSidebarNav = () => {
    setShowSidebar(false);
  };

  const redirectToMenu = (menu, level) => {
    if (!menu.sub_navigation.length) {
      closeSidebarNav();
    } else {
      if (level === "l2") {
        setShowSidebarNav(false);
        setSidebarl2Nav({
          state: true,
          title: menu.display,
          navigation: menu.sub_navigation,
        });
      }
      if (level === "l3") {
        setShowSidebarNav(false);
        setSidebarl3Nav({
          state: true,
          title: menu.display,
          navigation: menu.sub_navigation,
        });
      }
    }
  };

  return (
    <div className="font-body">
      <div className="icon">
          <svg-wrapper
            className="close-icon menu-icon"
            svg_src="close"
            onClick={closeSidebar}
          ></svg-wrapper>

      </div>
      <transition name="slideRight">

          <div className="sidebar">
            <div className="header-sidebar-navigation">
              <div className="search-bar">
                <svg-wrapper className="search-icon" svg_src="white-search"></svg-wrapper>
                <input
                  className="search-input"
                  // ref="searchInput"
                  type="text"
                  placeholder="What are looking for ?"
                />
                <button className="clear-btn" onClick={clearSearch}>Clear</button>
                <svg-wrapper
                  className="close-icon svg-solid-border"
                  svg_src="x-Menu"
                  onClick={closeSidebarNav}
                ></svg-wrapper>
              </div>
              <nav className="sidebar-navigation">
                <ul key="l1_Nav" className="l1_nav">
                  <transition-group name="fade" mode="out-in">
                    {getNavigation.map((nav, index) => (
                      <li className="l1_nav_border" key={index}>
                        <div className="label-with-icon" onClick={() => toggleChild(index)}>
                          <p className="l1_label">{nav.display}</p>
                          {nav.sub_navigation && nav.sub_navigation.length > 0 && (
                            <div>
                              <svg-wrapper
                                className="plus-icon svg-solid-border"
                                svg_src={isChildOpen(index) ? 'minus-red' : 'white-plus'}
                                onClick={() => toggleChild(index)}
                              ></svg-wrapper>
                            </div>
                          )}
                        </div>
                        <transition name="fade">
                          <ul className="l2_nav" v-if={nav.sub_navigation && isChildOpen(index)}>
                            {nav.sub_navigation.map((l2nav, l2index) => (
                              <li key={`l2_${l2index}`} className="l2_item">
                                <p className="l2_label">{l2nav.display}</p>
                              </li>
                            ))}
                          </ul>
                        </transition>
                      </li>
                    ))}
                  </transition-group>
                </ul>
                <div className="sidebar-footer">
                  <ul className="links">
                    <li>
                      <svg-wrapper className="media-icons" svg_src="ICONS"></svg-wrapper>
                      Find My Bike
                    </li>
                    <li>
                      <svg-wrapper className="media-icons" svg_src="location-r"></svg-wrapper>
                      Store Locator
                    </li>
                    <li>
                      <svg-wrapper className="media-icons" svg_src="Group"></svg-wrapper>
                      Register Your Bike
                    </li>
                    <li>
                      <svg-wrapper className="media-icons" svg_src="phone-r"></svg-wrapper>
                      Contact Us
                    </li>
                  </ul>
                  <ul className="social-icons">
                    <li>
                      <svg-wrapper className="social-icon" svg_src="facebook"></svg-wrapper>
                    </li>
                    <li>
                      <svg-wrapper className="social-icon" svg_src="insta"></svg-wrapper>
                    </li>
                    <li>
                      <svg-wrapper className="social-icon" svg_src="twitt"></svg-wrapper>
                    </li>
                    <li>
                      <svg-wrapper className="social-icon" svg_src="link"></svg-wrapper>
                    </li>
                    <li>
                      <svg-wrapper className="social-icon" svg_src="youtube"></svg-wrapper>
                    </li>
                    <li>
                      <svg-wrapper className="social-icon" svg_src="whp"></svg-wrapper>
                    </li>
                  </ul>
                </div>
              </nav>
            </div>
          </div>

      </transition>
    </div>
  );
}

export default Sidebar;
