import React from "react";

const BannerImage = ({ src, alt, style, aspectRatio }) => {
  const containerStyle = {
    position: 'relative',
    overflow: 'hidden',
    ...style
  };

  const imageStyle = {
    width: '100%',
    height: 'auto',
    display: 'block'
  };

  // Default aspect ratios
  const defaultAspectRatio = {
    desktop: '16:9',
    mobile: '4:3'
  };

  if (aspectRatio) {
    const { desktop = defaultAspectRatio.desktop, mobile = defaultAspectRatio.mobile } = aspectRatio;

    let aspectPercentage = 'auto';

    const setAspect = (ratio) => {
      const [width, height] = ratio.split(':').map(parseFloat);
      if (!isNaN(width) && !isNaN(height)) {
        aspectPercentage = (height / width) * 100;
      }
    };

    if (window.innerWidth >= 768) {
      setAspect(desktop);
    } else {
      setAspect(mobile);
    }

    containerStyle.paddingTop = aspectPercentage + '%';

    imageStyle.position = 'absolute';
    imageStyle.top = '0';
    imageStyle.left = '0';
    imageStyle.width = '100%';
    imageStyle.height = '100%';
  }
  return (
    <div style={containerStyle}>
        <img src={src} alt={alt} style={imageStyle} />
    </div>
  );
};

export default BannerImage;
