import React, { useEffect } from "react";
import Glide from "@glidejs/glide";

const Slider = ({ slides, starImagePath }) => {
  useEffect(() => {
    const glide = new Glide(".glide", {
      type: "slider",
      focusAt: "center",
      perView: 1,
      gap: 0,
      autoplay: 5000,
      hoverpause: true,
    });

    glide.mount();

    return () => glide.destroy();
  }, []);

  const renderStars = (starsCount) => {
    const stars = [];
    for (let i = 0; i < starsCount; i++) {
      stars.push(<img key={i} src={starImagePath} alt="star" />);
    }
    return stars;
  };
  return (
    <div className="glide">
      <div className="glide__track" data-glide-el="track">
        <ul className="glide__slides">
          {slides.map((slide, index) => (
            <li className="glide__slide" key={index}>
              <img src={slide.image} alt={`Slide ${index + 1}`} />
              <div className="detail-box">
                <div className="detail-content">
                <p>{renderStars(slide.stars)}</p>
                  <h2>{slide.heading}</h2>
                  <p>{slide.paragraph}</p>
                  <div className="horizontal-row"></div>
                </div>
                <div className="image-circle">
                  <img src={slide.circleImage} alt={`Circle  ${index + 1}`} />
                  <h2>{slide.name}</h2>
                  <p>{slide.circleText}</p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="glide__arrows" data-glide-el="controls">
        <button className="glide__arrow glide__arrow--prev" data-glide-dir="<">
          Prev
        </button>
        <button className="glide__arrow glide__arrow--next" data-glide-dir=">">
          Next
        </button>
      </div>
    </div>
  );
};

export default Slider;
