import React, { useState, useEffect } from "react";
import { isBrowser } from "browser-or-node";
import SvgWrapper from "../global/svg-wrapper";
import "./sidebar.css";

function Sidebar(props) {
  const [activeChildIndex, setActiveChildIndex] = useState(null);
  const [scrollY, setScrollY] = useState(0); // To store scroll position

  useEffect(() => {
    console.log("mounted NavData", props.context);
  }, []);

  useEffect(() => {
    // To stop document scroll when modal is active
    if (isBrowser) {
      if (props.isOpen) {
        document.body.style.position = "fixed";
        document.body.style.top = `-${scrollY}px`;
      } else {
        const scrollY = document.body.style.top;
        document.body.style.position = "";
        document.body.style.top = "";
        window.scrollTo(0, parseInt(scrollY || "0") * -1);
      }
    }
  }, [props.isOpen]);

  useEffect(() => {
    // Watch route changes
    const handleRouteChange = (to, from) => {
      if (to.path !== from.path) {
        closeSidebarNav();
      }
    };
    // Watch reset changes
    const handleResetChange = (newVal) => {
      if (newVal) {
        closeSidebarNav();
      }
    };

    return () => {
      // Cleanup
    };
  }, [props.$route, props.reset]);

  const toggleChild = (index) => {
    if (activeChildIndex === index) {
      setActiveChildIndex(null);
    } else {
      setActiveChildIndex(index);
    }
  }

  const isChildOpen = (index) => {
    return activeChildIndex === index;
  };

  const clearSearch = () => {
    // Code to clear search input value
  };


   const closeSidebarNav = () => {
    // Use closeSidebar function from props to close the sidebar
    props.closeSidebar();
  };

  const redirectToMenu = (menu, level) => {
    // Code for redirection
  };

  const getNavigation = () => {
    return props.context?.navigation?.slice(0, props.maxMenuLenght);
  };

  const isDesktop = () => {
    return props.context?.navigation?.length > 0 && !props.isSidebarNav;
  };

  const getMenuOffset = () => {
    let val = props.global_config?.props?.menu_layout_desktop;

    return val === "layout_1" || val === "layout_2"
      ? "padding-top: 20px"
      : "padding-top: 4px";
  };

  return (
    <div className="font-body">
      <div className="icon">
        <SvgWrapper
          className="close-icon menu-icon"
          src="close"
          onClick={closeSidebarNav}
        />
      </div>
      <div
        className="sidebar"
      >
        {/* Sidebar Content */}
        <div className="header-sidebar-navigation">
          {/* Header Content */}
          <div className="search-bar">
            {/* Search Bar Content */}
            <SvgWrapper className="search-icon" src="white-search" />
            <input
              className="search-input"
              ref="searchInput"
              type="text"
              placeholder="What are looking for ?"
            />
            <button className="clear-btn" onClick={clearSearch}>
              Clear
            </button>
            <SvgWrapper
              className="close-icon svg-solid-border"
              src="x-Menu"
              onClick={closeSidebarNav}
            />
          </div>
          {/* Navigation Content */}
          <nav className="sidebar-navigation">
            <ul key="l1_Nav" className="l1_nav">
              {getNavigation().map((nav, index) => (
                <li className="l1_nav_border" key={index}>
                  <div
                    className="label-with-icon"
                    onClick={() => toggleChild(index)}
                  >
                    <p className="l1_label">{nav.display}</p>
                    {nav.sub_navigation && nav.sub_navigation.length > 0 && (
                      <SvgWrapper
                        className="plus-icon svg-solid-border"
                        src={isChildOpen(index) ? "minus-red" : "white-plus"}
                        onClick={() => toggleChild(index)}
                      />
                    )}
                  </div>
                  <ul
                    className="l2_nav"
                    style={{ display: isChildOpen(index) ? "block" : "none" }}
                  >
                    {nav.sub_navigation &&
                      nav.sub_navigation.map((l2nav, l2index) => (
                        <li key={`l2_${l2index}`} className="l2_item">
                          <p className="l2_label">{l2nav.display}</p>
                        </li>
                      ))}
                  </ul>
                </li>
              ))}
            </ul>
            {/* Sidebar Footer */}
            <div className="sidebar-footer">
              <ul className="links">
                {/* Links Content */}
                <li>
                  <SvgWrapper className="media-icons" src="ICONS" />
                  Find My Bike
                </li>
                {/* More li elements */}
              </ul>
              <ul className="social-icons">
                {/* Social Icons Content */}
                <li>
                  <SvgWrapper className="social-icon" src="facebook" />
                </li>
                {/* More li elements */}
              </ul>
            </div>
          </nav>
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
