import React, { useEffect, useState } from "react";
import "./sidebar.css";
import SvgWrapper from "../global/svg-wrapper";

const context = {
  navigation: [
    {
      display: "Home",
      sub_navigation: [
        {
          display: "Sub Home 1",
          sub_navigation: [
            { display: "Sub Sub Home 1" },
            { display: "Sub Sub Home 2" },
          ],
        },
        {
          display: "Sub Home 2",
          sub_navigation: [
            { display: "Sub Sub Home 3" },
            { display: "Sub Sub Home 4" },
          ],
        },
      ],
    },
    {
      display: "About Us",
      sub_navigation: [{ display: "Sub About 1" }, { display: "Sub About 2" }],
    },
    {
      display: "Contact Us",
      sub_navigation: [
        { display: "Sub Contact 1" },
        { display: "Sub Contact 2" },
      ],
    },
  ],
};

function Sidebar({ closeSidebar }) {
  const [activeChildIndex, setActiveChildIndex] = useState(null);
  // const [isMounted, setIsMounted] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);
  const [showSidebarNav, setShowSidebarNav] = useState(true);
  const [sidebarl2Nav, setSidebarl2Nav] = useState({});
  const [sidebarl3Nav, setSidebarl3Nav] = useState({});
  const [mobileNavRedirectionLink] = useState({
    "contact-us": "",
    "store-locator": "/locate-us",
    "track-order": "",
    "country-selector": "",
  });

  useEffect(() => {
    setIsMounted(true);
    console.log("mounted NavData", context);
    return () => {
      setIsMounted(false);
    };
  }, []);

  useEffect(() => {
    // Watch route changes
    const handleRouteChange = () => {
      closeSidebarNav();
    };
    window.addEventListener("popstate", handleRouteChange);
    return () => {
      window.removeEventListener("popstate", handleRouteChange);
    };
  }, []);

  useEffect(() => {
    // To stop document scroll when modal is active
    if (showSidebar) {
      document.body.style.position = "fixed";
      document.body.style.top = `-${window.scrollY}px`;
    } else {
      const scrollY = parseInt(document.body.style.top || "0");
      document.body.style.position = "";
      document.body.style.top = "";
      window.scrollTo(0, Math.abs(scrollY));
    }
  }, [showSidebar]);

  const getNavigation = context?.navigation?.slice(0, 100);
  const isDesktop = context?.navigation?.length > 0 && !"isSidebarNav";

  const getMenuOffset = () => {
    let val = "layout_1";
    return val === "layout_1" || val === "layout_2"
      ? "padding-top: 20px"
      : "padding-top: 4px";
  };

  const toggleChild = (index) => {
    setActiveChildIndex((prevIndex) => (prevIndex === index ? null : index));
  };

  const isChildOpen = (index) => {
    return activeChildIndex === index;
  };

  const clearSearch = () => {
    // Handle clearing search input
  };

  const getGlobalConfigValue = () => {
    // Implement this function
  };

  const openSidebarNav = () => {
    setShowSidebar(true);
  };

  const closeSidebarNav = () => {
    setShowSidebar(false);
  };

  const redirectToMenu = (menu, level) => {
    if (!menu.sub_navigation.length) {
      closeSidebarNav();
    } else {
      if (level === "l2") {
        setShowSidebarNav(false);
        setSidebarl2Nav({
          state: true,
          title: menu.display,
          navigation: menu.sub_navigation,
        });
      }
      if (level === "l3") {
        setShowSidebarNav(false);
        setSidebarl3Nav({
          state: true,
          title: menu.display,
          navigation: menu.sub_navigation,
        });
      }
    }
  };

  return (
    <div className="font-body">
      <div className="icon">
        <SvgWrapper
          className="close-icon menu-icon"
          src="close"
          onClick={closeSidebar}
        ></SvgWrapper>
      </div>
      <transition name="slideRight">
        <div className="sidebar">
          <div className="header-sidebar-navigation">
            <div className="search-bar">
              <SvgWrapper
                className="search-icon"
                src="white-search"
              ></SvgWrapper>
              <input
                className="search-input"
                // ref="searchInput"
                type="text"
                placeholder="What are looking for ?"
              />
              <button className="clear-btn" onClick={clearSearch}>
                Clear
              </button>
              <SvgWrapper
                className="close-icon svg-solid-border"
                src="x-Menu"
                onClick={closeSidebarNav}
              ></SvgWrapper>
            </div>
            <nav className="sidebar-navigation">
              <ul key="l1_Nav" className="l1_nav">
                <transition-group name="fade" mode="out-in">
                  {getNavigation.map((nav, index) => (
                    <li className="l1_nav_border" key={index}>
                      <div
                        className="label-with-icon"
                        onClick={() => toggleChild(index)}
                      >
                        <p className="l1_label">{nav.display}</p>
                        {nav.sub_navigation &&
                          nav.sub_navigation.length > 0 && (
                            <div>
                              <SvgWrapper
                                className="plus-icon svg-solid-border"
                                src={
                                  isChildOpen(index)
                                    ? "minus-red"
                                    : "white-plus"
                                }
                                onClick={() => toggleChild(index)}
                              ></SvgWrapper>
                            </div>
                          )}
                      </div>
                      <transition name="fade">
                        <ul
                          className="l2_nav"
                          v-if={nav.sub_navigation && isChildOpen(index)}
                        >
                          {nav.sub_navigation.map((l2nav, l2index) => (
                            <li key={`l2_${l2index}`} className="l2_item">
                              <p className="l2_label">{l2nav.display}</p>
                            </li>
                          ))}
                        </ul>
                      </transition>
                    </li>
                  ))}
                </transition-group>
              </ul>
              <div className="sidebar-footer">
                <ul className="links">
                  <li>
                    <SvgWrapper
                      className="media-icons"
                      src="ICONS"
                    ></SvgWrapper>
                    Find My Bike
                  </li>
                  <li>
                    <SvgWrapper
                      className="media-icons"
                      src="location-r"
                    ></SvgWrapper>
                    Store Locator
                  </li>
                  <li>
                    <SvgWrapper
                      className="media-icons"
                      src="Group"
                    ></SvgWrapper>
                    Register Your Bike
                  </li>
                  <li>
                    <SvgWrapper
                      className="media-icons"
                      src="phone-r"
                    ></SvgWrapper>
                    Contact Us
                  </li>
                </ul>
                <ul className="social-icons">
                  <li>
                    <SvgWrapper
                      className="social-icon"
                      src="facebook"
                    ></SvgWrapper>
                  </li>
                  <li>
                    <SvgWrapper
                      className="social-icon"
                      src="insta"
                    ></SvgWrapper>
                  </li>
                  <li>
                    <SvgWrapper
                      className="social-icon"
                      src="twitt"
                    ></SvgWrapper>
                  </li>
                  <li>
                    <SvgWrapper className="social-icon" src="link"></SvgWrapper>
                  </li>
                  <li>
                    <SvgWrapper
                      className="social-icon"
                      src="youtube"
                    ></SvgWrapper>
                  </li>
                  <li>
                    <SvgWrapper className="social-icon" src="whp"></SvgWrapper>
                  </li>
                </ul>
              </div>
            </nav>
          </div>
        </div>
      </transition>
    </div>
  );
}

export default Sidebar;
