import React, { useEffect, useRef } from "react";
import Glide from "@glidejs/glide";
import "@glidejs/glide/dist/css/glide.core.min.css";
import "@glidejs/glide/dist/css/glide.theme.min.css";
import ProductCard from "./product-Card";

const GlideSlider = ({ slides, className }) => {
  const glideRef = useRef(null);

  useEffect(() => {
    const glide = new Glide(glideRef.current, {
      type: "slider",
      perView: 5,
      gap: 30, // Adjust as needed
      focusAt:'center',
      peek: {
        before: 0,
        after: 0,
      },
      breakpoints: {
        800: {
          perView: 3,
        },
        600: {
          perView: 2,
        },
        480: {
          perView: 1,
        },
      },
    });

    glide.on(["mount.after", "run"], () => {
      const slides = glideRef.current.querySelectorAll(".glide__slide");
      slides.forEach((slide, index) => {
        const middleIndex = Math.floor(slides.length / 2);
        const opacity = index === middleIndex ? 1 : 0.5;
        slide.style.opacity = opacity;
      });
    });

    glide.mount();

    return () => {
      glide.destroy();
    };
  }, [slides]);

  return (
    <div className={`glide ${className}`} ref={glideRef}>
      <div className="glide__track" data-glide-el="track">
        <ul className="glide__slides">
          {slides.map((slide, index) => (
            <li className="glide__slide" key={index}>
              <ProductCard
                image={slide.image}
                title={slide.title}
                description={slide.description}
                className="product-card"
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default GlideSlider;
